Send-MailMessage -To "yang2sr@mail.uc.edu" -From "lingge120@gmail.com" -Subject "IT3038C Windows SysInfo" -Body ("This machine's IP is" + $IP.toString()+"." +" User is $env:USERNAME. Hostname is $env:COMPUTERNAME. PowerShell Version" + $Host.Version.Major.ToString() + ". Today's Date is " + (Get-Date).DayofWeek.ToString()+ ", " +(Get-Date).Month.ToString()+"/" +(Get-Date).Day.ToString()+"/"+ (Get-Date).Year.ToString()) -SmtpServer smtp.gmail.com -port 587 -UseSSL -Credential (Get-Credential)